sys_script.script:
var logger = new Logger();

(function executeRule(current, previous /*null when async*/) {


	logger.info("Inserting to compliance import");
	//logger.info(current.reports);
	try {
		var assetData = JSON.parse(current.data);

		var complianceImport = new GlideRecordSecure('x_chef_automate_compliance_import');

		complianceImport.initialize();
		if (complianceImport.canCreate()) {
			logger.info(gs.getMessage("User {0} can insert into x_chef_automate_compliance_import", gs.getUser().getName()));
		} else {
			logger.warn(gs.getMessage("User {0} cannot insert into x_chef_automate_compliance_import", gs.getUser().getName()));
		}
		logger.info("Asset data length ".concat(assetData.length));

		// Map Request to Table
		for(var entry = 0; entry < assetData.length; entry++) {
			var asset =  assetData[entry];
			if (isEmpty(asset.report)) {
				logger.info("There is no compliance report to process");
				continue;
			}
			MapToImportTable(complianceImport, asset, logger);
			if (complianceImport.insert() === null) {
				var message = "Insert failed into x_chef_automate_compliance_import";
				logger.warn(message);
			} else {
				logger.info("Insert succeeded into x_chef_automate_compliance_import");
			}
		}
	} catch (err) {
		logger.error(err);
		logger.error(err.stack);
	}


})(current, previous);

function isEmpty(report) {
	for(var key in report) {
		return false;
	}
	return true;
}

function MapToImportTable(complianceImport, asset, logger){
	try {

			var report = asset.report;
			var ohai = getObject(asset, "automatic");
			complianceImport.host_name = getStringValue(ohai, "hostname");
			complianceImport.mac_address = getStringValue(ohai, "macaddress");
			logger.info(gs.getMessage("asset report {0}", report));

			complianceImport.id = getStringValue(report, "id");
			complianceImport.node_id = getStringValue(report, "node_id");
			complianceImport.node_name = getStringValue(report, "node_name");
			var endTime = getObject(report, "end_time");
			complianceImport.end_time = getDateValue(getIntValue(endTime, "seconds"));
			complianceImport.status = getStringValue(report, "status");
			complianceImport.environment = getStringValue(report, "environment");
			complianceImport.version = getStringValue(report, "version");
			complianceImport.job_id = getStringValue(report, "job_id");
			complianceImport.ip_address = getStringValue(report, "ipaddress");
			complianceImport.fqdn = getStringValue(report, "fqdn");
			complianceImport.controls = JSON.stringify(report.controls);
			complianceImport.platform = JSON.stringify(report.platform);
			complianceImport.statistics = JSON.stringify(report.statistics);
			complianceImport.profiles = JSON.stringify(report.profiles);

	} catch (err) {
		logger.error('Chef Automate Import Table Mapping Error: '.concat(err.message));
		logger.error(err.stack);
		throw err;
	}
}

function getStringValue(object, value) {
	try {
		return object[value];
	} catch (err) {
		logger.warn(err);
		return "";
	}
}
function getIntValue(object, value) {
	try {
		logger.info(gs.getMessage("get int value: {0}", object[value]));
		return object[value];
	} catch (err) {
		logger.warn(err);
		return null;
	}
}

function getDateValue(seconds) {
	try {
		logger.info(gs.getMessage("seconds {0}", seconds));
		var d = 1000 * seconds;
		var date = new Date(1000 * seconds);
		logger.info(gs.getMessage("seconds {0}, millis {1}, date {2}", [seconds, d, date.toISOString()]));
		return date.toISOString();
	} catch (err) {
		logger.warn(err);
		return null;
	}
}

function getObject(object, value) {
	try {
		return object[value];
	} catch (err) {
		logger.warn(err);
		return {};
	}
}
