sys_script_include.script:
var JsonUtil = Class.create();
JsonUtil.prototype = {
    initialize: function() {
		this.logger = new Logger();
		this.util = new Util();
    },

	getStringValue: function (object, value) {
		try {
			return object[value];
		} catch (err) {
			logger.warn(err);
			return "";
		}
	},

	getIntValue: function(object, value) {
		try {
			return object[value];
		} catch (err) {
			logger.warn(err);
			return null;
		}
	},

	getDecimalValue: function(object, value) {
		try {
			return object[value];
		} catch (err) {
			logger.warn(err);
			return null;
		}
	},

	getObject: function(object, value) {
		try {
			return object[value];
		} catch (err) {
			logger.warn(err);
			return {};
		}
	},

	stringify: function(object) {
		try {
			return JSON.stringify(object);
		}  catch (err) {
			logger.warn(err);			
			return "";
		}
	},

	getDateValue: function (seconds) {
		try {
			return this.util.getISODate(seconds);
		} catch (err) {
			logger.warn(err);
			return "";
		}
	},

	parse: function(string) {
		try {
			return JSON.parse(string);
		}  catch (err) {
			logger.warn(err);
			return {};
		}	
	},

	getStringValueFromArray: function(object, name, index) {
		try {
			return object[name][index];
		} catch (err) {
			logger.warn(err);
			return "";
		}
	},

    type: 'Json'


};
