sys_script_include.script:
var JsonUtil = Class.create();
JsonUtil.prototype = {
    initialize: function() {
    },

	getStringValue: function (object, value) {
		try {
			return object[value];
		} catch (err) {
			logger.warn(err);
			return "";
		}
	},

	getIntValue: function(object, value) {
		try {
			return object[value];
		} catch (err) {
			logger.warn(err);
			return null;
		}
	},

	getObject: function(object, value) {
		try {
			return object[value];
		} catch (err) {
			logger.warn(err);
			return {};
		}
	},

	stringify: function(object) {
		try {
			return JSON.stringify(object);
		}  catch (err) {
			logger.warn(err);
			return "";
		}
	},

	getDateValue: function (seconds) {
		try {
			logger.debug(gs.getMessage("seconds {0}", seconds));
			var d = 1000 * seconds;
			var date = new Date(1000 * seconds);
			logger.debug(gs.getMessage("seconds {0}, millis {1}, date {2}", [seconds, d, date.toISOString()]));
			return date.toISOString();
		} catch (err) {
			logger.warn(err);
			return null;
		}
	},

	parse: function(string) {
		try {
			return JSON.parse(string);
		}  catch (err) {
			logger.warn(err);
			return {};
		}	
	},

    type: 'JsonUtil'


};
