sys_transform_map.script:
(function transformRow(source, target, map, log, isUpdate) {

	// Add your code here
	var logger = new Logger();
	try {
	// look up the node from bleh
		logger.info("***** TRANSFORMING TO LINK SERVER TABLE TO REPORT *******");
		logger.info(gs.getMessage("Source IP {0}", source.ip_address));

		var server = new GlideRecordSecure("cmdb_ci_server");
		server.initialize();
		server.addQuery("ip_address", source.ip_address);
		server.query();
		if (server.next()) {
			target.node = server.sys_id;
			logger.info(gs.getMessage("Server found for ip address {0}", source.ip_address));
		} else {
			logger.info(gs.getMessage("No server found for ip {0}", source.ip_address));
		}

		// platform
		var platform = JSON.parse(source.platform);
		var platformName = platform.name;
		var platformRelease = platform.release;
		logger.info(gs.getMessage("platform {0} {1}", [platformName, platformRelease]));
		target.platform_name = platformName;
		target.platform_release = platformRelease;

		// stats
		var duration = JSON.parse(source.statistics).duration;
		target.duration = duration;
	} catch (err) {
		logger.error(err);
		logger.error(err.stack);
	}

})(source, target, map, log, action==="update");
