sys_ws_operation.operation_script:
(function process(/*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {
    var logger = new Logger();
	var jsonUtil = new JsonUtil();
	try {
		// Set Properties
		var propertyData = jsonUtil.parse(request.body.dataString);
		gs.setProperty("x_chef_automate.ipAddress", propertyData['ipAddress']);
		gs.setProperty("x_chef_automate.macAddress", propertyData['macAddress']);
		gs.setProperty("x_chef_automate.hostname", propertyData['hostname']);
		gs.setProperty("x_chef_automate.logging.enabled",propertyData['logs']);

		response.setBody({"ipAddress":gs.getProperty("x_chef_automate.ipAddress"), 
			"macAddress":gs.getProperty("x_chef_automate.macAddress"),
			"hostname":gs.getProperty("x_chef_automate.hostname"),
			"logs":gs.getProperty("x_chef_automate.logging.enabled")});
	} catch (err) {
		logger.errorWithStack("REST API call /api/x_chef_automate/asset error", err);
		error = new sn_ws_err.ServiceError();
		error.setStatus(500);
		error.setMessage(err.message);
		response.setError(error);
	}

})(request, response);
