sys_ws_operation.operation_script:
(function process(/*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {
	var logger = new Logger();
	logger.info("REST API call /api/x_chef_automate/asset");
	try {
		var assetData = request.body.dataString;
		logger.info(gs.getMessage("asset data: {0}", assetData));

		var assetsBufferTable = new GlideRecordSecure('x_chef_automate_asset_buffer');
		assetsBufferTable.initialize();
		if (assetsBufferTable.canCreate()) {
			logger.info(gs.getMessage("User {0} can insert into x_chef_automate_asset_buffer", gs.getUser().getName()));
		}
		else {
			logger.warn(gs.getMessage("User {0} cannot insert into x_chef_automate_asset_buffer", gs.getUser().getName()));
		}

				// Add Request to Buffer
		MapToBufferTable(assetsBufferTable, assetData, logger);
		var assetId = assetsBufferTable.insert();
		if (assetId === null) {
			var message = "Insert failed into x_chef_automate_asset_import";
			logger.warn(message);
			var error = new sn_ws_err.ServiceError();
			error.setStatus(500);
			error.setMessage("Insert failed into x_chef_automate_asset_import");
			response.setError(error);
		} else {
			logger.info("Insert succeeded into x_chef_automate_asset_import");
			response.setStatus(201);
			response.setContentType('application/json');
			response.setBody({"Sys_Id": assetId});
			}
	} catch (err) {
		logger.error(err);
		error = new sn_ws_err.ServiceError();
		error.setStatus(500);
		error.setMessage(err.message);
		response.setError(error);
	}

})(request, response);

function MapToBufferTable(assetsBufferTable, assetData, logger){
	try {
		assetsBufferTable.data = assetData;
	} catch (err) {
		logger.error('Chef Automate Buffer Table Mapping Error: '.concat(err.message));
		logger.error(err);
		throw err;
	}
}
