sysauto_script.script:
var logger = new Logger();
var complianceReportsPerNode = 5;

try {
	logger.info('Executing Scheduled Script Delete Compliance reports');
	complianceReportsPerNode = parseInt(gs.getProperty('x_chef_automate.compliance_reports_per_node'));
	var agg = new GlideAggregate('x_chef_automate_compliance_report');
	agg.addAggregate('COUNT', 'node');
	agg.query();
	while (agg.hasNext()) {
		agg.next();
		agg.getAggregate('COUNT', 'node');
		processClientRuns(agg);
	}
} catch(err) {
	logger.errorWithStack("Delete Compliance reports runs", err);
}

function processClientRuns(aggregate) {
	var node = aggregate.node;
	var query = new GlideRecordSecure('x_chef_automate_compliance_report');
	query.addQuery('node', node);
	query.orderByDesc('sys_created_on');
	query.query();
	var rowCount = query.getRowCount();
	var skipCount = 0;
	while (query.hasNext()) {
		query.next();
		skipCount++;
		if (skipCount <= complianceReportsPerNode) {
			continue;
		}
		query.deleteRecord();
	}
}
