sysauto_script.script:
var logger = new Logger();
var clientRunsPerNode = 5;

try {
	logger.info('Executing Scheduled Script Delete Client runs');
	clientRunsPerNode = parseInt(gs.getProperty('x_chef_automate.client_runs_per_node'));
	var agg = new GlideAggregate('x_chef_automate_client_run');
	agg.addAggregate('COUNT', 'node');
	agg.query();
	var clientRuns = 0;
	while (agg.hasNext()) {
		agg.next();
		clientRuns = agg.getAggregate('COUNT', 'node');
		processClientRuns(agg);
	}
} catch(err) {
	logger.errorWithStack("Delete Client runs", err);
}

function processClientRuns(aggregate) {
	var node = aggregate.node;
	var query = new GlideRecordSecure('x_chef_automate_client_run');
	query.addQuery('node', node);
	query.orderByDesc('sys_created_on');
	query.query();
	var rowCount = query.getRowCount();
	var skipCount = 0;
	while (query.hasNext()) {
		query.next();
		skipCount++;
		if (skipCount <= clientRunsPerNode) {
			continue;
		}
		query.deleteRecord();
	}
}
